//vari√°veis do jogo
var canvas, ctx, ALTURA, LARGURA, maxPulos = 3, velocidade = 4, estadoAtual, record, img, personagem,
    
pontosParaNovaFase = [10, 30, 50, 70, 90, 120, 150, 170, 200, 250],
faseAtual = 0,
   
labelNovaFase = {
texto: "",
opacidade: 0.0,
  
fadeIn: function(dt){
 var fadeInId = setInterval(function() {
    if (labelNovaFase.opacidade < 1.0)
      labelNovaFase.opacidade += 0.01;
   
   else{
     clearInterval(fadeInId);
   }
  }, 10 * dt);

},
  
fadeOut: function(dt){
 var fadeOutId = setInterval(function() {
    if (labelNovaFase.opacidade > 0.0)
      labelNovaFase.opacidade -= 0.01;
    
    else{
     clearInterval(fadeOutId);
   }
  }, 10 * dt);
}
},
    
estados = {
jogar: 0,
jogando: 1,
perdeu: 2
},    
    
chao = {
y: 540,
x: 0,
altura: 50,

  
atualiza: function(){
this.x -= velocidade;
if (this.x <= -56)
  this.x += 56;
},
  
desenha: function(){
spriteChao.desenha(this.x, this.y);
spriteChao.desenha(this.x + spriteChao.largura, this.y);
}
},

bloco = {
x: 50,
y: 0,
altura: spriteBoneco.altura,
largura: spriteBoneco.largura,
gravidade: 1.8,
velocidade: 0,
forcaDoPulo: 23.6,
qntPulos: 0,
score: 0,
  
atualiza: function(){
this.velocidade += this.gravidade;
this.y += this.velocidade;
  
if (this.y > chao.y - this.altura && estadoAtual != estados.perdeu){
    this.y = chao.y - this.altura;
    this.qntPulos = 0;
    this.velocidade = 0;
}  
}, 
pula: function(){
if (this.qntPulos < maxPulos)
 this.velocidade = -this.forcaDoPulo;
 this.qntPulos++;
},
reset: function(){
this.velocidade = 0;
this.y = 0;

  
if (this.score > record) {
localStorage.setItem ("record", this.score);
record = this.score;
}
  
velocidade = 4;
faseAtual = 0;
this.gravidade = 1.8;
  
this.score = 0;
},
desenha: function(){
spriteBoneco.desenha(this.x, this.y);
}
},
obstaculos = {
  _obs: [],
  _scored: false,
  sprites: [spriteObstacle],
  tempoInsere: 0,
  insere: function(){
  this._obs.push({
  x: LARGURA,
  // largura: 30 + Math.floor(21 * Math.random()),
  largura: 50,
  y: chao.y - Math.floor(30 + Math.random() * 90),
   sprite: this.sprites[Math.floor(this.sprites.length * Math.random())] 
  });
    this.tempoInsere = 40 + Math.floor(21 * Math.random());
  },
atualiza: function() {
if (this.tempoInsere == 0)
  this.insere();
else
  this.tempoInsere--;
  
for (var i = 0, tam = this._obs.length; i < tam; i++) {
var obs = this._obs[i];
  
obs.x -= velocidade;
  
if (bloco.x < obs.x + obs.largura && bloco.x + bloco.largura >= obs.x && bloco.y + bloco.altura >= obs.y) 
estadoAtual = estados.perdeu;

else if (obs.x <= 0 && !obs._scored){
  bloco.score++;
  obs._scored = true;
  
  if (faseAtual < pontosParaNovaFase.length && bloco.score == pontosParaNovaFase[faseAtual])
    passarDeFase();
}
  
else if (obs.x <= -obs.largura){
this._obs.splice(i, 1);
tam--;
i--;
}  
}
},
  
limpa: function() {
this._obs = [];
},
  
desenha: function() {
for (var i = 0, tam = this._obs.length; i < tam; i++) {
var obs = this._obs[i];
obs.sprite.desenha(obs.x, obs.y);
}
}  
  
};

function clique(event) {
if (estadoAtual == estados.jogando) 
  bloco.pula();
 
else if (estadoAtual == estados.jogar) {
  estadoAtual = estados.jogando;
}
  
else if (estadoAtual == estados.perdeu && bloco.y >= 2 * ALTURA) {
  estadoAtual = estados.jogar;
  obstaculos.limpa();
  bloco.reset();
} 
}

function passarDeFase() {
  velocidade ++;
  faseAtual ++;
  
  if (faseAtual == 3)
    bloco.gravidade = 1.0;
  
  
  labelNovaFase.texto = "Level " + faseAtual;
  labelNovaFase.fadeIn(0.4);
  
  setTimeout(function() {
    labelNovaFase.fadeOut(0.4);
  }, 800);
}


function main() {
 ALTURA = window.innertHeight;
 LARGURA = window.innerWidth;
  
 if (LARGURA >= 500){
     LARGURA = 608;
     ALTURA = 590;
 }

  
canvas = document.createElement("canvas");
canvas.width = LARGURA;
canvas.height = ALTURA;
canvas.style.border = "1px solid #000";


ctx = canvas.getContext("2d");
document.body.appendChild(canvas);
  
document.addEventListener("mousedown", clique); 

estadoAtual = estados.jogar;
record = localStorage.getItem("record");
  
if (record == null)
  record = 0;
img = new Image(1);
img.src = "data:image/png;base64,"

roda();
}
function roda() {
atualiza();
desenha();
  
window.requestAnimationFrame(roda);
  
}
function atualiza() {
  
  chao.atualiza();
  
  bloco.atualiza();
  if (estadoAtual == estados.jogando)
  obstaculos.atualiza();
  
}
function desenha() {
ctx.fillStyle = "#0000CD";
ctx.fillRect(0, 0, LARGURA, ALTURA);
 
bg.desenha(0, 0);
  
ctx.fillStyle = "#fff";
ctx.font = "50px Arial";
ctx.fillText(bloco.score, 30, 68);

ctx.fillStyle = "rgba(255, 255, 255, " + labelNovaFase.opacidade + ")";
ctx.fillText(labelNovaFase.texto, canvas.width / 2 - ctx.measureText(labelNovaFase.texto).width / 2, canvas.height / 3);
  
if (estadoAtual == estados.jogar){
ctx.fillStyle = "green";
ctx.fillRect(LARGURA / 2 - 50, ALTURA / 2 - 50,100,100);
 } 
else if (estadoAtual == estados.perdeu) {
ctx.fillStyle = "red";
ctx.fillRect(LARGURA / 2 - 50, ALTURA / 2 - 50,100,100);
  
ctx.save();
ctx.translate(ALTURA / 2, LARGURA / 2);
ctx.fillStyle = "#fff";
  
if (bloco.score > record)
  ctx.fillText("New Record!", -150, -65);
  
else if (record < 10)
  ctx.fillText("Record " + record, -89, -65);
  
else if (record >=10 && record < 100)
  ctx.fillText("Record " + record, -112, -65);
  
else 
  ctx.fillText("Record " + record, -125, -65);
if (bloco.score < 10)
  ctx.fillText(bloco.score, -7, 10);
  
else if (bloco.score >= 10 && bloco.score < 100)
  ctx.fillText(bloco.score, -20, 10);
  
  else 
   ctx.fillText(bloco.score, -32, 10);
  
ctx.restore();
}
  
else if (estadoAtual == estados.jogando)
         obstaculos.desenha();

chao.desenha();  
bloco.desenha();
}


//inicializa o jogo
main();